def infofrompiuscores(level):
    response = requests.get(f"https://piuscores.arroweclip.se/api/tierlist/passcount?ChartType=Single&Level={level}", headers={
        'accept':'*/*',
        'Authorization':'Basic VEhFUkVJU0FCT01CU1RSQVBQRURUT01ZQ0hFU1Q6NzEwYTExZjMtMmVjNi00OGViLTlkNTEtMjgxMTY2NzJkNDM1'
    })
    request=response.json()
    i=0
    try:
        while True:
            name = str(request[i]["chart"]["song"]["name"])
            i+=1
            if name not in str(namesretrieved):
                namesretrieved.append(name)
    except:
        pass
            


def similarity(string):
    i = 0
    while i != len(songs):
        howsimilar = sm(None,string,songs[i]).ratio()
        if howsimilar >= 0.8:
            similarsongname = songs[i]
            break
        if howsimilar < 0.8:
            i+=1
    if i == len(songs):
        similarsongname = None
    return similarsongname


